import React, { createContext, useState } from 'react';
import uuid from 'uuid/v1'

export const BookContext = createContext();

const BookContextProvider = (props) => {
    // books = original data and setBooks = to change the state or data
    const [books, setBooks] = useState([
        { id: 12343, title: 'Name of the wind', author: '09/16/2019'},
        { id: 212412, title: 'The final empire', author: '08/17/2019'},
        { id: 35123123, title: 'The final empire', author: '08/17/2019'}

    ]);
    console.log(books);

    const addBook = (title, author) => {
        setBooks([...books, {id: uuid(),title: title, author: author}]);
    };

    const removeBook = (id) => {
        setBooks([books.filter(book => book.id !== id)])
        console.log(id)
    }

    return ( 
        <BookContext.Provider value = {{books, addBook, removeBook}}>
            {props.children}
        </BookContext.Provider>
     );
}

export default BookContextProvider;
