import uuid from 'uuid/v4'

export const BookReducer = (state, action) => {
    switch(action.type){
        case 'Add_Book':
            return ([...state, {title: action.book.title, date: action.book.date, id: uuid()}]);
        case 'Add_Remove':
            return (state.filter(book => book.id !== action.id))
        default:
            return state
    }
}
