import React, { useContext } from 'react';
import  { ListContext } from '../contexts/ListContext';
import {ThemeContext} from '../contexts/ThemeContext'
import { isEmptyStatement } from '@babel/types';



const ListDetails = ({ list }) => {
    const {dispatch} = useContext(ListContext)
    const{passFuncTheme} = useContext(ThemeContext);
    const{completed, line, noLine} = useContext(ThemeContext);
    const theme = completed ? line : noLine;

    const dayLeftModifier = () => {
        var date = list.dateLeft + 1;
        console.log(typeof(date))
        if(date < 0 || date === isEmptyStatement ){
            date = 0;
        }    
        return date;
    }
    return (                 // removeBook(book.id)  // onClick = {() => dispatch({type: 'Remove_List', id: list.id})}
        <li className="single-list" style={{textDecoration: theme.textDecoration}} onClick = {passFuncTheme.bind(this,list.id)}>
            <button className = "btn" 
                        type = 'button'
                        onClick = {() => dispatch({type: 'Remove_List', id: list.id})}>X
            </button>
            <div className="title">{list.title} <span className = "date">{dayLeftModifier()} days left</span></div>
            <div className="author">{list.date}</div>


        </li>
     );
}

export default ListDetails;
