import React, { useContext } from 'react';
import  { ListContext } from '../contexts/ListContext';
import {ThemeContext} from '../contexts/ThemeContext'



const ListDetails = ({ list }) => {
    const {dispatch} = useContext(ListContext)
//    / const{passFuncTheme} = useContext(ThemeContext);
    const{completed, line, noLine} = useContext(ThemeContext);
    const theme = completed ? line : noLine;

    const dayLeftModifier = () => {
        var date = list.dateLeft;
        var dayStr = '';
        if(isNaN(date) ){
            dayStr = `enter date`
        }  
        else if(date <= 0){
            date = 0;
            dayStr = `${date} day left`
        }
        else{
            date = date + 1
            dayStr = `${date} days left`
        }  
        return dayStr;
    }
    const passComplete = (id) =>{
        var value = list.completedBool;
        if(value){
             document.getElementById('text').style.textDecoration = "line-through"

        }
        else{
            list.completed = true;
        }
    }
    return (                 // removeBook(book.id)  // onClick = {() => dispatch({type: 'Remove_List', id: list.id})}
        <li className="single-list" id = 'text' style={{textDecoration: theme.textDecoration}} onClick = {passComplete.bind(this, list.id)}>
            <button className = "btn" 
                        type = 'button'
                        onClick = {() => dispatch({type: 'Remove_List', id: list.id})}>X
            </button>
            <div className="title">{list.title} <span className = "date">{dayLeftModifier()}</span></div>
            <div className="author">{list.date}</div>


        </li>
     );
}

export default ListDetails;
