import uuid from 'uuid/v4'

export const ListReducer = (state, action) => {
    var str = '';
    const lineThrough = () => {
        if(list.id == action.id ){
            str = "line through"
        }
        return str;
    }
    switch(action.type){
        case 'Add_List':
            return ([...state, {title: action.list.title, date: action.list.date,dateLeft: action.list.dateLeft ,id: uuid()}]);
        case 'Remove_List':
            return (state.filter(list => list.id !== action.id)) //state.filter(list => list.id !== action.id)
        case 'Text_line':
            return(lineThrough);
        default:
            return state
    }
}
