import  { BookContext } from '../contexts/BookContext'


const BookDetails = ({passBook}) => {
    const {removeBook} = useContext(BookContext)
    return ( 
        <li>
            <div className="title">{passBook.title}</div>
            <div className="author">{passBook.Date}</div>
        </li>
     );
}

export default BookDetails;
