import React, { createContext, useState, useEffect } from 'react';
import uuid from 'uuid/v1'

export const BookContext = createContext();

const BookContextProvider = (props) => {
    // books = original data and setBooks = to change the state or data
    const [books, setBooks] = useState([], () => {
        try{
            const localData = window.localStorage.getItem(key);
            return localData ? JSON.parse(books) : [];
        }
        catch(error){
            console.log(error);
            return [];
        }
    });

    // store the data in local storage
    useEffect(() =>{
       window.localStorage.setItem(key, JSON.stringify(books))
    }, [books]);


    const addBook = (title, date) => {
        setBooks([...books, {title: title, date: date, id: uuid()}]);
    };

    const removeBook = (id) => {
        setBooks(books.filter(book => book.id !== id))

    }

    return ( 
        <BookContext.Provider value = {{books, addBook, removeBook}}>
            {props.children}
        </BookContext.Provider>
     );
}

export default BookContextProvider;
