import React, {useContext, useState } from 'react';
import { ListContext } from '../contexts/ListContext';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import format from 'date-fns/format'


const AddListForm = () => {
    const{dispatch} = useContext(ListContext);
    const[title, setTitle] = useState("");
    const [date, setDate] = useState('');

    const [startDate, setStartDate] = useState(new Date());



    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(date)
        dispatch({type: 'Add_List', list: {title, date}})
        setTitle('');
        setDate('');
    }
    const handleChange = (date) => {
        const output = date.format;
        console.log(output)
         setStartDate(date)
    }


    return ( 
        <form onSubmit = {handleSubmit} >
            <input 
                type ='text' 
                placeholder= 'what is your plan for today?' 
                value = {title}
                onChange = {(e) => setTitle(e.target.value)}>
            </input>
            {/* <input 
                type ='text' 
                placeholder= 'Due Date' 
                value = {date}
                onChange = {(e) => setDate(e.target.value)}>
            </input> */}
            <div>
                <DatePicker selected={startDate} onChange={(value) =>setDate(handleChange.value)}   value = {date} placeholderText ='Due Date' />
            </div>

            <input type = 'submit' value = 'Add List'/>
        </form>

     );
}

export default AddListForm;
